    <script>
        // Get DOM elements
        const form = document.getElementById('calculatorForm');
        const ticketPriceInput = document.getElementById('ticketPrice');
        const customerVolumeInput = document.getElementById('customerVolume');
        const ticketPriceError = document.getElementById('ticketPriceError');
        const customerVolumeError = document.getElementById('customerVolumeError');
        const calculateBtn = document.getElementById('calculateBtn');
        const resetBtn = document.getElementById('resetBtn');
        const resultsSection = document.getElementById('resultsSection');

        // Result display elements
        const baselineRevenueEl = document.getElementById('baselineRevenue');
        const monthlyIncreaseLowEl = document.getElementById('monthlyIncreaseLow');
        const monthlyIncreaseHighEl = document.getElementById('monthlyIncreaseHigh');
        const annualIncreaseLowEl = document.getElementById('annualIncreaseLow');
        const annualIncreaseHighEl = document.getElementById('annualIncreaseHigh');

        // Format number as USD currency with commas
        function formatCurrency(value) {
            return new Intl.NumberFormat('en-US', {
                style: 'currency',
                currency: 'USD',
                minimumFractionDigits: 2,
                maximumFractionDigits: 2
            }).format(value);
        }

        // Validate input: must be a positive number
        function validateInput(value, fieldName) {
            // Remove any whitespace
            value = value.trim();

            // Check if empty
            if (value === '') {
                return { valid: false, error: `${fieldName} is required.` };
            }

            // Try to parse as number
            const numValue = parseFloat(value);

            // Check if it's a valid number
            if (isNaN(numValue)) {
                return { valid: false, error: `${fieldName} must be a valid number.` };
            }

            // Check if it's positive
            if (numValue <= 0) {
                return { valid: false, error: `${fieldName} must be greater than 0.` };
            }

            return { valid: true, value: numValue };
        }

        // Display error message
        function showError(inputElement, errorElement, message) {
            errorElement.textContent = message;
            inputElement.setAttribute('aria-invalid', 'true');
            inputElement.classList.add('border-red-500');
            inputElement.classList.remove('border-gray-300');
        }

        // Clear error message
        function clearError(inputElement, errorElement) {
            errorElement.textContent = '';
            inputElement.setAttribute('aria-invalid', 'false');
            inputElement.classList.remove('border-red-500');
            inputElement.classList.add('border-gray-300');
        }

        // Calculate and display results
        function calculateResults() {
            // Get and validate ticket price
            const ticketPriceValidation = validateInput(
                ticketPriceInput.value, 
                'Average Ticket Price'
            );

            // Get and validate customer volume
            const customerVolumeValidation = validateInput(
                customerVolumeInput.value, 
                'Monthly Customer Volume'
            );

            // Show errors if validation fails
            let hasErrors = false;

            if (!ticketPriceValidation.valid) {
                showError(ticketPriceInput, ticketPriceError, ticketPriceValidation.error);
                hasErrors = true;
            } else {
                clearError(ticketPriceInput, ticketPriceError);
            }

            if (!customerVolumeValidation.valid) {
                showError(customerVolumeInput, customerVolumeError, customerVolumeValidation.error);
                hasErrors = true;
            } else {
                clearError(customerVolumeInput, customerVolumeError);
            }

            // Don't calculate if there are errors
            if (hasErrors) {
                resultsSection.classList.add('hidden');
                return;
            }

            // Perform calculations
            const ticketPrice = ticketPriceValidation.value;
            const customerVolume = customerVolumeValidation.value;

            // Baseline Monthly Revenue = Average Ticket Price * Monthly Customer Volume
            const baselineMonthlyRevenue = ticketPrice * customerVolume;

            // Projected Revenue Increase: 5% (low) and 10% (high)
            const monthlyIncreaseLow = baselineMonthlyRevenue * 0.05;
            const monthlyIncreaseHigh = baselineMonthlyRevenue * 0.10;

            // Annual Revenue Increase (monthly range * 12)
            const annualIncreaseLow = monthlyIncreaseLow * 12;
            const annualIncreaseHigh = monthlyIncreaseHigh * 12;

            // Update display with formatted currency values
            baselineRevenueEl.textContent = formatCurrency(baselineMonthlyRevenue);
            monthlyIncreaseLowEl.textContent = formatCurrency(monthlyIncreaseLow);
            monthlyIncreaseHighEl.textContent = formatCurrency(monthlyIncreaseHigh);
            annualIncreaseLowEl.textContent = formatCurrency(annualIncreaseLow);
            annualIncreaseHighEl.textContent = formatCurrency(annualIncreaseHigh);

            // Show results section
            resultsSection.classList.remove('hidden');
        }

        // Reset form and results
        function resetForm() {
            ticketPriceInput.value = '';
            customerVolumeInput.value = '';
            clearError(ticketPriceInput, ticketPriceError);
            clearError(customerVolumeInput, customerVolumeError);
            resultsSection.classList.add('hidden');
            // Focus on first input for better accessibility
            ticketPriceInput.focus();
        }

        // Debounce function to limit how often calculations run
        let debounceTimer;
        function debounceCalculate(delay = 300) {
            clearTimeout(debounceTimer);
            debounceTimer = setTimeout(() => {
                calculateResults();
            }, delay);
        }

        // Event Listeners

        // Form submission
        form.addEventListener('submit', (e) => {
            e.preventDefault();
            calculateResults();
        });

        // Auto-update on input change (debounced)
        ticketPriceInput.addEventListener('input', () => {
            debounceCalculate();
        });

        customerVolumeInput.addEventListener('input', () => {
            debounceCalculate();
        });

        // Reset button
        resetBtn.addEventListener('click', resetForm);

        // Allow only numbers and decimal point in inputs
        ticketPriceInput.addEventListener('keypress', (e) => {
            // Allow: backspace, delete, tab, escape, enter, decimal point, and numbers
            if (e.key === 'Backspace' || e.key === 'Delete' || e.key === 'Tab' || 
                e.key === 'Escape' || e.key === 'Enter') {
                return;
            }
            // Allow decimal point only once
            if (e.key === '.' && ticketPriceInput.value.includes('.')) {
                e.preventDefault();
                return;
            }
            // Only allow numbers and decimal point
            if (!/[0-9.]/.test(e.key)) {
                e.preventDefault();
            }
        });

        customerVolumeInput.addEventListener('keypress', (e) => {
            // Allow: backspace, delete, tab, escape, enter, and numbers
            if (e.key === 'Backspace' || e.key === 'Delete' || e.key === 'Tab' || 
                e.key === 'Escape' || e.key === 'Enter') {
                return;
            }
            // Only allow numbers (no decimal for customer volume)
            if (!/[0-9]/.test(e.key)) {
                e.preventDefault();
            }
        });
    </script>
