        const retentionInput = document.getElementById('retention');

        const revenueResult = document.getElementById('revenue-result');
        const dealsResult = document.getElementById('deals-result');
        const roiResult = document.getElementById('roi-result');
        const dealFraction = document.getElementById('deal-fraction');

        const BUILDIFY_ANNUAL_COST = 7800; // $650/month √ó 12

        // Parse currency input to number
        function parseCurrency(value) {
            return parseInt(value.replace(/[^0-9]/g, '')) || 0;
        }

        // Format number as currency
        function formatCurrency(value) {
            return new Intl.NumberFormat('en-US', {
                style: 'currency',
                currency: 'USD',
                minimumFractionDigits: 0,
                maximumFractionDigits: 0
            }).format(value);
        }

        // Format commission input with commas
        function formatCommissionInput(value) {
            const num = parseCurrency(value);
            if (num === 0) return '';
            return num.toLocaleString('en-US');
