            // Assumes 1 in 20 retained contacts converts per year
            const additionalDeals = Math.round((volume * retention) / 20 * 10) / 10;

            // Calculate revenue increase
            const revenueIncrease = Math.round(additionalDeals * commission);

            // Calculate ROI
            const roi = Math.round(((revenueIncrease - BUILDIFY_ANNUAL_COST) / BUILDIFY_ANNUAL_COST) * 100);

            // Calculate what fraction of a deal pays for Buildify
            const dealsToPayOff = BUILDIFY_ANNUAL_COST / commission;
            let dealFractionText;
            if (dealsToPayOff < 1) {
                dealFractionText = `${Math.round(dealsToPayOff * 100)}% of 1 deal`;
            } else if (dealsToPayOff < 2) {
                dealFractionText = '1 closed deal';
            } else {
                dealFractionText = `${Math.ceil(dealsToPayOff)} closed deals`;
            }

            // Update display with animation
            revenueResult.textContent = formatCurrency(revenueIncrease);
            dealsResult.textContent = additionalDeals % 1 === 0 ? additionalDeals : additionalDeals.toFixed(1);
            roiResult.textContent = roi > 0 ? `${roi}%` : '‚Äî';
            dealFraction.textContent = dealFractionText;
        }

        // Commission input formatting
        commissionInput.addEventListener('input', (e) => {
            const cursorPos = e.target.selectionStart;
            const oldLength = e.target.value.length;
            e.target.value = formatCommissionInput(e.target.value);
            const newLength = e.target.value.length;

            // Adjust cursor position for added/removed commas
            const newCursorPos = Math.max(0, cursorPos + (newLength - oldLength));
            e.target.setSelectionRange(newCursorPos, newCursorPos);

            calculate();
        });

        // Volume slider
        volumeSlider.addEventListener('input', () => {
            volumeDisplay.textContent = volumeSlider.value;
            calculate();
        });

        // Retention buttons
        retentionButtons.forEach(btn => {
            btn.addEventListener('click', () => {
                // Update visual state
                retentionButtons.forEach(b => {
                    b.classList.remove('bg-brand-blue', 'text-white', 'border-brand-blue');
