    <script>
        const ticketInput = document.getElementById('ticket');
        const customersInput = document.getElementById('customers');
        const lowValue = document.getElementById('lowValue');
        const highValue = document.getElementById('highValue');
        const lowROI = document.getElementById('lowROI');
        const highROI = document.getElementById('highROI');
        const monthlyBtn = document.getElementById('monthlyBtn');
        const annualBtn = document.getElementById('annualBtn');
        const roiToggle = document.getElementById('roiToggle');
        const roiSection = document.getElementById('roiSection');
        const valueLabel = document.getElementById('valueLabel');

        let isAnnual = false;
        let roiVisible = false;

        const MONTHLY_COST = 650;
        const ANNUAL_COST = MONTHLY_COST * 12;

        function formatUSD(value) {
            return value.toLocaleString("en-US", {
                style: "currency",
                currency: "USD",
                maximumFractionDigits: 0
            });
        }

        function formatPercent(value) {
            return value.toFixed(0) + '%';
        }

        function calculate() {
            const ticket = parseFloat(ticketInput.value);
            const customers = parseInt(customersInput.value, 10);

            if (!ticket || !customers) {
                lowValue.textContent = "$0";
                highValue.textContent = "$0";
                lowROI.textContent = "0%";
                highROI.textContent = "0%";
                return;
            }

            const baseRevenue = ticket * customers;
            let incrementalLow = baseRevenue * 0.05;
            let incrementalHigh = baseRevenue * 0.10;
            let cost = MONTHLY_COST;

            if (isAnnual) {
                incrementalLow *= 12;
                incrementalHigh *= 12;
                cost = ANNUAL_COST;
            }

            // Net Monthly/Annual Value = Incremental Revenue - Cost
            const netLow = incrementalLow - cost;
            const netHigh = incrementalHigh - cost;

            lowValue.textContent = formatUSD(netLow);
            highValue.textContent = formatUSD(netHigh);

            // ROI % = (Net Value / Cost) * 100
            const roiLow = (netLow / cost) * 100;
            const roiHigh = (netHigh / cost) * 100;

            lowROI.textContent = formatPercent(roiLow);
            highROI.textContent = formatPercent(roiHigh);
        }

        ticketInput.addEventListener('input', calculate);
        customersInput.addEventListener('input', calculate);

        monthlyBtn.addEventListener('click', () => {
            isAnnual = false;
            monthlyBtn.classList.add('bg-gray-900', 'text-white');
            monthlyBtn.classList.remove('bg-white', 'text-gray-700');
            annualBtn.classList.add('bg-white', 'text-gray-700');
            annualBtn.classList.remove('bg-gray-900', 'text-white');
            valueLabel.textContent = 'Net Monthly Value (After Buildify Cost)';
            calculate();
        });

        annualBtn.addEventListener('click', () => {
            isAnnual = true;
            annualBtn.classList.add('bg-gray-900', 'text-white');
            annualBtn.classList.remove('bg-white', 'text-gray-700');
            monthlyBtn.classList.add('bg-white', 'text-gray-700');
            monthlyBtn.classList.remove('bg-gray-900', 'text-white');
            valueLabel.textContent = 'Net Annual Value (After Buildify Cost)';
            calculate();
        });

        roiToggle.addEventListener('click', () => {
            roiVisible = !roiVisible;
            if (roiVisible) {
                roiSection.classList.remove('hidden');
                roiToggle.textContent = 'Hide ROI %';
            } else {
                roiSection.classList.add('hidden');
                roiToggle.textContent = 'Show ROI %';
            }
        });
    </script>   
